mapper.select:
SELECT * FROM bbs_article
WHERE article_subno =
#{article_subno}
AND board_id = #{board_id}

mapper.update:
UPDATE bbs_article
SET hit = hit + 1
WHERE article_subno = #{article_subno}
AND   board_id      = #{board_id     }

UPDATE bbs_article
SET good = good + 1
WHERE article_subno = #{article_subno}
AND   board_id      = #{board_id     }

UPDATE bbs_article
SET bad = bad + 1
WHERE article_subno = #{article_subno}
AND   board_id      = #{board_id     }

mapper.select:
SELECT article_subno, article_title, article_content, article_regdate, user_id, good, hit FROM (SELECT @RNUM := @RNUM + 1 r, a.* FROM (SELECT article_subno, article_title, article_content, article_regdate, user_id, good, hit FROM bbs_article WHERE board_id = #{board_id}
AND article_title LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY article_subno desc) a ,(select @RNUM:=0) b) temp WHERE r BETWEEN #{start} AND #{end};
