mapper.select:
SELECT * FROM bbs_article
WHERE article_subno =
#{article_subno}
AND board_id = #{board_id}

mapper.update:
UPDATE bbs_article
SET hit = hit + 1
WHERE article_no = #{article_no}

UPDATE bbs_article
SET good = good + 1
WHERE article_no = #{article_no}

UPDATE bbs_article
SET bad = bad + 1
WHERE article_no = #{article_no}
