SELECT article_subno, article_title, article_content, article_regdate, user_id, article_good, article_hit FROM (SELECT @RNUM := @RNUM + 1 r, a.* FROM (SELECT article_subno, article_title, article_content, article_regdate, user_id, article_good, article_hit FROM bbs_article WHERE article_use = 1 AND board_id = #{board_id}
AND article_title LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY article_subno desc) a ,(select @RNUM:=0) b) temp WHERE r BETWEEN #{start} AND #{end};

SELECT * FROM bbs_article
WHERE board_id = #{board_id}
AND article_subno = #{article_subno}
